      $(function () {
        $(".ShowMobileMenu").click(function () {

          $(".my_menu_body").fadeIn();
          $(".my_menu_tab").animate({
            left: "0px"
          });
          $(".MenuIconBar").hide();
          $(".MenuIconClose").fadeIn();

        });

        $(".ShowMobileMenuExit").click(function () {
          $(".my_menu_tab").animate({
            left: "-76%"
          });
          $(".my_menu_body").fadeOut();
          $(".MenuIconClose").hide();
          $(".MenuIconBar").fadeIn();
        });

        $('.my_menu_body').click(function (event) {
          if (!$('.my_menu_tab').is(event.target) && $('.my_menu_tab').has(event.target).length === 0) {
            $(".my_menu_tab").animate({
              left: "-76%"
            });
            $(".my_menu_body").fadeOut();
            $(".MenuIconClose").hide();
            $(".MenuIconBar").fadeIn();
          }
        });
      });
      $(document).on('click', '.LogOut', function () {
        var action = 'userLogOut';
        var rurl = '';
        $('.all_msg1').html('<div class="load"><i></i></div>');
        $.post('./__allaction.php', {
          action: action,
          rurl: rurl
        }, function (data) {
          $(".all_msg1").html(data);
        });
      });
